import React, { useEffect, useState } from "react";
import { View, Text } from "react-native";
import { useSelector, useDispatch } from "react-redux";
import Todo from "../components/Todo";
import { addTodo } from "../Redux/slices/Todo.slice";

const CompletedTodos = () => {
  const [doneTodos, setDoneTodos] = useState([]);
  const { todos } = useSelector((state) => state.todo);
  const dispatch = useDispatch();

  useEffect(() => {
    if (todos) {
      const filteredTodos = todos.filter((todo) => todo.done);
      setDoneTodos(filteredTodos);
    }
  }, [todos]);

  const handleAddTodo = () => {
    // Modify this function according to your requirements
    const newTodo = {
      id: Math.random().toString(),
      title: "New Todo",
      done: false,
    };
    dispatch(addTodo(newTodo));
  };

  return (
    <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
      {doneTodos.length ? (
        <>
          {doneTodos.map((todo) => (
            <Todo key={todo.id} todo={todo} />
          ))}
        </>
      ) : (
        <Text>No Todos Found!</Text>
      )}

      <T title="Add Todo" onPress={handleAddTodo} />
    </View>
  );
};

export default CompletedTodos;
