import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  TextInput,
  Platform,
} from "react-native";
import React, { useEffect, useState } from "react";
import Todos from "../components/Todos";
import AsyncStorage from '@react-native-async-storage/async-storage';
const Home = ({}) => {
  const [todos, setTodos] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");

  useEffect(()=>{
trackStorage();
},[])
const trackStorage =async()=>{
  const asyncTodos=await AsyncStorage.getItem('todos');
  if(asyncTodos) 
  setTodos(JSON.parse(asyncTodos))
}
  const addTodo = async () => {
    if (title) {
      const singleTodo = {
        id: Date.now(),
        done: false,
        title,
        description,
      };

      // Check if the todo already exists
      const existingTodo = todos.find((todo) => todo.title === title);

      // If the todo doesn't exist, add it to the todos array
      if (!existingTodo) {
        const allTodos = [...todos, singleTodo];
        await AsyncStorage.setItem('todos',JSON.stringify(allTodos));

        setTodos(allTodos);
      }
    }


  };

  return (
    <View
      style={{
        flex: 1,
        alignItems: "center",
        justifyContent: "flex-start",
        paddingTop: 50,
        backgroundColor: Platform.OS === "ios" ? "black" : "gainsboro",
      }}
    >
      <Text style={styles.title}>ToDo List </Text>
      <TextInput
        onChangeText={(value) => setTitle(value)}
        style={styles.input}
        placeholder="Enter Your Title"
      />
      <TextInput
        onChangeText={(value) => setDescription(value)}
        style={styles.input}
        placeholder="Enter Your Description"
      />
      <TouchableOpacity style={styles.button} onPress={addTodo}>
        <Text style={{ color: "#fff", fontSize: 20 }}>Save</Text>
      </TouchableOpacity>
      {todos.length !== 0 && (
        <>
          <View style={styles.divider} />
          <Todos todos={todos} />
        </>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  title: {
    fontWeight: "500",
    fontSize: 25,
    marginBottom: 15,
  },
  input: {
    borderWidth: 1,
    borderColor: "green",
    borderRadius: 5,
    paddingVertical: 10,
    paddingHorizontal: 15,
    marginBottom: 15,
    width: 300,
    textAlign: "center",
  },
  button: {
    padding: 5,
    backgroundColor: "black",
    borderRadius: 5,
    height: 40,
    paddingHorizontal: 40,
  },
  divider: {
    width: "95%",
    height: 1,
    backgroundColor: "gray",
    marginVertical: 20,
  },
});
export default Home;



