import { View, Text } from "react-native";
import React, { useEffect } from "react";
import { useRoute } from "@react-navigation/native";

const TodoDetails = () => {
  const params = useRoute().params;
  return (
    <View style={{
      borderWidth:5,
      width:400,
      minHeight:50,
      margin:'auto'

      }} >

      {params && (
        <>
          <Text style={{ textAlign: 'center', fontSize: 'xx-large', color: 'black', margin: '30px', }}>{params.title}</Text>
          <Text style={{ textAlign: 'center', fontSize: 'xx-large', color: 'black' }}>{params.description}</Text>
        </>
      )}
    </View>
  );
};

export default TodoDetails;
