import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  TextInput,
  Platform,
} from "react-native";
import AsyncStorage from '@react-native-async-storage/async-storage';

const Home = ({}) => {
  const [todos, setTodos] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");

  const addTodo = () => {
    if (title) {
      const singleTodo = {
        id: Date.now(),
        done: false,
        title,
        description,
      };

      // Check if the todo already exists
      const existingTodo = todos.find((todo) => todo.title === title);

      // If the todo doesn't exist, add it to the todos array
      if (!existingTodo) {
        const allTodos = [...todos, singleTodo];
        setTodos(allTodos);
      }
    }
  };

  useEffect(() => {
    trackStorage();
  }, []);

  const trackStorage = async () => {
    const asyncTodos = await AsyncStorage.getItem('todos');
    if (asyncTodos) 
      setTodos(JSON.parse(asyncTodos));
  };

  // const addTodo = async () => {
  //   if (title) {
  //     const singleTodo = {
  //       id: Date.now(),
  //       done: false,
  //       title,
  //       description,
  //     };

  //     // Check if the todo already exists
  //     const existingTodo = todos.find((todo) => todo.title === title);

  //     // If the todo doesn't exist, add it to the todos array
  //     if (!existingTodo) {
  //       const allTodos = [...todos, singleTodo];
  //       await AsyncStorage.setItem('todos', JSON.stringify(allTodos));
  //       setTodos(allTodos);
  //     }
  //   }
  // };

  const deleteTodo = async (id) => {
    const updatedTodos = todos.filter((todo) => todo.id !== id);
    await AsyncStorage.setItem('todos', JSON.stringify(updatedTodos));
    setTodos(updatedTodos);
  };

  return (
    <View
      style={{
        flex: 1,
        alignItems: "center",
        justifyContent: "flex-start",
        paddingTop: 50,
        backgroundColor: Platform.OS === "ios" ? "black" : "gainsboro",
      }}
    >
      <Text style={styles.title}>ToDo APP </Text>
      <TextInput
        onChangeText={(value) => setTitle(value)}
        style={styles.input}
        placeholder="Enter Your Title"
      />
      <TextInput
        onChangeText={(value) => setDescription(value)}
        style={styles.input}
        placeholder="Enter Your Description"
      />
      <TouchableOpacity style={styles.button} onPress={addTodo}>
        <Text style={{ color: "#fff", fontSize: 20 }}>SUBMITE</Text>
      </TouchableOpacity>
      <View style={styles.divider} />
      {todos.length !== 0 && (
        <>


          {todos.map((todo) => (

            <View  style={styles.todoContainer}>

              <Text style={styles.todoTitle}>{todo.title}</Text>
              <Text style={styles.todoDescription}>{todo.description}</Text>
              <TouchableOpacity
                style={styles.deleteButton}
                onPress={() => deleteTodo(todo.id)}
              >
                <Text style={{ color: "PINK" }}>Delete</Text>
              </TouchableOpacity>
            </View>
          ))}
        </>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  title: {
    fontWeight: "500",
    fontSize: 25,
    marginBottom: 15,
  },
  input: {
    borderWidth: 1,
    borderColor: "gray",
    borderRadius: 8,
    paddingVertical: 10,
    paddingHorizontal: 15,
    marginBottom: 15,
    width: 300,
    textAlign: "center",

  },
  button: {
    padding: 5,
    backgroundColor: "gray",
    borderRadius: 5,
    height: 40,
    paddingHorizontal: 40,
  },
  divider: {
    width: "95%",
    height: 2,
    backgroundColor: "gray",
    marginVertical: 20,
  },
  todoContainer: {
    marginBottom: 15,
    alignItems: "center",
  },
  todoTitle: {
    fontWeight: "500",
    fontSize: 20,
  },
  todoDescription: {
    fontSize: 16,
  },
  deleteButton: {
    marginTop: 5,
    padding: 5,
    backgroundColor: "red",
    borderRadius: 5,
    height: 30,
    paddingHorizontal: 15,
    justifyContent: "center",
    alignItems: "center",
  },
});

export default Home;
